punctuation = ['(',')','[',']',',','-','.','?','!',':',';','#','&'] ## stopword and punctuation list
output_dict = {}

ponys = ["Twilight Sparkle", "Applejack","Rarity", "Pinkie Pie", "Rainbow Dash", "Fluttershy"] ## list of characters for analysis

#%% WORD COUNT COMPUTATION

for pony in ponys:
    
    temp = data[data.pony == pony]
    
    for char in punctuation:
        temp['dialog'] = temp['dialog'].str.lower().str.replace(char,' ',regex=True) ## replace punctuation with space
    
    freq_data = temp.dialog.str.split(expand=True).stack().value_counts().reset_index()
    freq_data.columns = ['Word', 'Frequency'] ## generate columns for word count df
    
    for word in stop_words:
        freq_data = freq_data[freq_data.Word != word] ## remove stopwords
    
    freq_data = freq_data[freq_data.Frequency > 4]
    freq_data['Alphabetical'] = freq_data['Word'].str.isalpha() ## remove non-alphabetical
    freq_data = freq_data[freq_data.Alphabetical == True]
    del(freq_data['Alphabetical'])
    output_dict[pony.lower()] = freq_data.set_index('Word').T.to_dict('index')['Frequency'] ## populate output dictionary with stats

#%% JSON Generation

out_path = sys.argv[2]
with open(os.path.join(os.path.dirname(__file__), out_path), 'w') as result:
    json.dump(output_dict, result)
    
    
    